example.code:
private void ShowReport()
{
	// Load the main report
	FileInfo rdlxFile = new FileInfo(Path.Combine(AppPath, "ParentReport.rdlx"));
	ReportDefinition parentReportDefinition = new ReportDefinition(rdlxFile);
	// Load the subreport
	rdlxFile = new FileInfo(Path.Combine(AppPath, "MySubreport.rdlx"));
	ReportDefinition subreportDefinition = new ReportDefinition(rdlxFile);
	// Create a ReportResource for the subreport
	ReportResource[] resources = new ReportResource[] {new ReportResource("MySubreport", subreportDefinition)};
	// Specify the subreport as an available resource to the main report
	parentReportDefinition.ResourceResolver.SetAvailableResources( resources );

	ReportRuntime report = new ReportRuntime(parentReportDefinition);

	ImageRenderingExtension re = new ImageRenderingExtension();
	report.Render(re, new FileStreamProvider( new DirectoryInfo(AppPath),"myparentreport" ) );
}
